mapper.insert:


         SELECT 
            seq_comment.nextval comment_num
         from dual


	        insert into 
	        comments(
	             comment_num
	            ,board_num
	            ,cmem_id
	            ,content
	           ) 
	        values
	           (
	             #{commentNum}
	            ,#{boardNum}
	            ,#{memId}
	            ,#{content}
	           )

mapper.select:
select * from (
    select * from comments
    where board_num = #{boardNum} 
    order by comment_num desc
) A

where rownum <= 5

mapper.update:
UPDATE 
   comments 
set 
     content = #{content}

WHERE mem_id = #{memId}
   AND
     comment_num = #{commentNum}

mapper.delete:
DELETE FROM
 comments
WHERE mem_id = #{memId}
   AND
     comment_num = #{commentNum}
